INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (20, 'subsub_question', 18, '(ii) State two reasons why a programmer would prefer to use hexadecimal numbers rather than binary numbers. 1 ........................................................................................................................................ 2 ........................................................................................................................................', 'short_answer', 2);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (21, 'sub_question', 16, 'Show the denary value 6.25 as a floating point binary number representing the mantissa and exponent. Both of these should be stored in two’s complement representation. You should use as few bits as possible. Show your working. .............................................................................................................................................. [3]', 'short_answer', 3);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (22, 'sub_question', 16, 'State the benefit of using a normalised form when representing data as a floating point number. .............................................................................................................................................. [1]', 'short_answer', 1);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (23, 'question', NULL, 'OCRConfectionery is a sweet manufacturing company. They want to use a relational database to store details of the orders their customers make.', 'short_answer', NULL);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (24, 'sub_question', 23, 'State two benefits of using a relational database instead of a flat file database. 1 ................................................................................................................................................ 2 ................................................................................................................................................ [2]', 'short_answer', 2);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (25, 'sub_question', 23, 'One customer can order as many different products as they like in the same order. A customer can also place as many orders as they like. One product can be ordered multiple times in the same order or ordered by multiple different customers. Complete the entity relationship diagram to show the relationships between the Product, Customer and Order entities. [2] Customer Product Order', 'short_answer', 2);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (26, 'sub_question', 23, 'The order table has these fields. OrderID OrderDate OrderAmount CustomerID ProductID', 'short_answer', NULL);
