INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (28, 'subsub_question', 26, '(ii) State one foreign key in the order table. ......................................................................................................................................', 'short_answer', 1);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (29, 'subsub_question', 26, '(iii) State why CustomerID would not make a suitable primary key in the order table. ......................................................................................................................................', 'short_answer', 1);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (30, 'question', NULL, 'Amaya is an amateur photographer and has bought an old second-hand computer to edit her photographs. The specifications of this computer are shown below. Processor: Dual Core 1.8 GHz RAM: 1 GB HDD: 500 GB', 'short_answer', NULL);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (31, 'sub_question', 30, 'State the role of RAM in a computer system. .............................................................................................................................................. [1]', 'short_answer', 1);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (32, 'sub_question', 30, 'Explain what is meant by the term ‘virtual memory’ and why this may be needed when Amaya is editing her photographs. .............................................................................................................................................. [2]', 'short_answer', 2);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (33, 'question', NULL, 'Zac has an accountancy business. He is moving into an office that has enough space for up to five members of staff. Zac would like to install a Local Area Network (LAN) to allow his staff to work together.', 'short_answer', NULL);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (34, 'sub_question', 33, '', 'short_answer', NULL);
