INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (36, 'subsub_question', 34, '(ii) Explain why packet switching is more suitable for a computer network than circuit switching. ......................................................................................................................................', 'short_answer', 2);
