INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (1, 'question', NULL, 'A program uses a bubble sort to sort data into ascending numerical order. The data is stored in a 0-indexed 1-dimensional array.', 'short_answer', NULL);
