INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (20, 'question', NULL, 'Nina is writing a computer game using an Integrated Development Environment (IDE). Her friend, James, is writing a computer game using a text-editor which will allow James to create and edit text. James will use a separate compiler. Discuss the differences between writing and debugging a program using an IDE and a text-editor. You should include the following in your answer: • features that are used when writing code • features that are used when debugging code • the benefits of using an IDE instead of a text-editor. ..................................................................................................................................................... [9]', 'short_answer', 9);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (21, 'question', NULL, 'A function, toBinary(), is needed to calculate the binary value of a denary integer between 0 and 255. toBinary() needs to: • take an integer value as a parameter • divide the number by 2 repeatedly, storing a 1 if it has a remainder and a 0 if it doesn’t • combine the remainder values (first to last running right to left) to create the binary number • return the binary number. For example, to convert 25 to a binary number the steps are as follows:  6 / 2 = 3 remainder 0 3 / 2 = 1 remainder 1 1 / 2 = 0 remainder 1 return value = 11001', 'code_block', NULL);
INSERT INTO question_bank (id, level, parent_id, text, question_type, marks) VALUES (22, 'sub_question', 21, 'Write the function toBinary(). You should write your function using pseudocode or program code. .............................................................................................................................................. [6]', 'short_answer', 6);
